for i in %device_mountpoint_tuple% ; do IFS=","; set $i; if [ -e $1 ] ; then echo "$1 is existed"; if grep $1 /etc/mtab > /dev/null 2>&1; then echo "$1 was mounted, umounting it..."; %sudo_command% umount $1 ; else echo "$1 was not mounted" ; fi ; echo "making filesystem and mounting $1" ; %sudo_command% mkfs -t ext4 $1 ; %sudo_command% mkdir -p -m 777 $2 ; %sudo_command% mount $1 $2 ; else echo "$i is not existed, ignoring it" ; fi ; done && echo '%task_id%' >> %succeedtasks_filepath%
